(function executeRule(current, previous /*null when async*/) {

	var rec = new GlideRecord('x_58872_needit_needit');
	var rightnow = new GlideDateTime();
	rec.addQuery('u_when_needed','<=',rightnow);
	rec.addEncodedQuery('u_request_type=legal^ORu_request_type=hr');
	rec.query();
	rec.setValue('short_description','test');
	rec.updateMultiple();


	var count = new GlideAggregate('x_58872_needit_needit');
	count.addQuery('u_when_needed','<=',rightnow);
	count.addEncodedQuery('u_request_type=legal^ORu_request_type=hr');
	count.addAggregate('COUNT');
	count.query();
	if(count.next()){
		gs.addInfoMessage(count.getAggregate('COUNT'));
	}

