sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //This code is simply trying to fetch the email from the requestor record ..but couldnot fetch as client script only has access to form fields. need to use script include to fetch the email id from the record based on the requestor
   var requestor = g_form.getValue('u_requested_for');
	jslog("This message is from jslog().");
	jslog("The requestor is " +  requestor);
	try{
		var requestorMailId = requestor.email;
		jslog("The email id is " + requestorMailId);
	   g_form.setValue('u_requested_for_email',requestorMailId);
	}

	catch(err){
		alert('A JavaScript runtime error occurred: ' + err.message);
		jslog('A JavaScript runtime error occurred: ' + err.message);
	}

}
