sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    compareDate: function(strDate) {
        var rightNow = new GlideDateTime();
        var dateSelected = new GlideDateTime(strDate);
        if (dateSelected.before(rightNow)) {
            gs.addErrorMessage("please select an appropriate date");
            return false;
        } else {
            return true;
        }

    },
    checkingForToday: function(strDate) {
        var rightnow = new GlideDateTime();
        var today = rightnow.getDate();
        var whenNeeded = new GlideDateTime(strDate);
        var neededDate = whenNeeded.getDate();
        if (neededDate.equals(today)) {
            gs.addErrorMessage("Please select a future date");
            return false;
        } else {
            return true;
        }
    },
    type: 'NeedItUtils'
};
