sys_script_include.script:
var userutils = Class.create();
userutils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getemail: function() {
        try {          
            var tableObj = new GlideRecord('sys_user');
            tableObj.get(this.getParameter('sysparm_userId'));
            gs.addInfoMessage('email is ' + tableObj.email);
            return tableObj.email;
        } catch (err) {
            gs.addErrorMessage(err.message);
        }
    },
    type: 'userutils'
});
